import{_ as s,c as e,o as a,d as n}from"./app.a5526522.js";const C=JSON.parse('{"title":"ssh每隔一段时间不操作，便会自动断开连接","description":"","frontmatter":{},"headers":[{"level":2,"title":"原因：","slug":"原因","link":"#原因","children":[]},{"level":2,"title":"解决方法：","slug":"解决方法","link":"#解决方法","children":[]},{"level":2,"title":"重启sshd服务","slug":"重启sshd服务","link":"#重启sshd服务","children":[]},{"level":2,"title":"本文引用","slug":"本文引用","link":"#本文引用","children":[]}],"relativePath":"sidebar/document/linux/ssh-service.md","lastUpdated":1671366272000}'),l={name:"sidebar/document/linux/ssh-service.md"},t=n(`<h1 id="ssh每隔一段时间不操作-便会自动断开连接" tabindex="-1">ssh每隔一段时间不操作，便会自动断开连接 <a class="header-anchor" href="#ssh每隔一段时间不操作-便会自动断开连接" aria-hidden="true">#</a></h1><h2 id="原因" tabindex="-1">原因： <a class="header-anchor" href="#原因" aria-hidden="true">#</a></h2><p>Linux安全设置问题：如果 60s 内没有任何数据，将会自动断开 SSH 连接。</p><h2 id="解决方法" tabindex="-1">解决方法： <a class="header-anchor" href="#解决方法" aria-hidden="true">#</a></h2><p>使用 <code>vim /etc/ssh/sshd_config</code> 编辑如下两行：</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;"># 客户端每隔多少秒向服务发送一个心跳数据</span></span>
<span class="line"><span style="color:#A6ACCD;">ClientAliveInterval 30</span></span>
<span class="line"><span style="color:#A6ACCD;"># 客户端多少秒没有相应，服务器自动断掉连接</span></span>
<span class="line"><span style="color:#A6ACCD;">ClientAliveCountMax 1800</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><h2 id="重启sshd服务" tabindex="-1">重启sshd服务 <a class="header-anchor" href="#重启sshd服务" aria-hidden="true">#</a></h2><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">service sshd restart</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><h2 id="本文引用" tabindex="-1">本文引用 <a class="header-anchor" href="#本文引用" aria-hidden="true">#</a></h2><p><a href="https://blog.csdn.net/qq_28296925/article/details/83828828" target="_blank" rel="noreferrer">https://blog.csdn.net/qq_28296925/article/details/83828828</a></p>`,10),i=[t];function r(c,d,o,h,p,_){return a(),e("div",null,i)}const A=s(l,[["render",r]]);export{C as __pageData,A as default};
